def merge_lib_dirs(dir1: str, dir2: str) -> str:
    # 分割输入字符串，得到目录列表
    dirs1 = set(dir1.split(';'))
    dirs2 = set(dir2.split(';'))
    
    # 计算并集
    merged_dirs = dirs1.union(dirs2)
    
    # 重新合并为一行字符串，用分号分隔
    return ';'.join(sorted(merged_dirs))

